 # This is the login page
def loginframe():
    sub = Frame(main, width=820, height=500, bg="#0d0d0b").place(x=0, y=0)
    loginimage = Label(sub, image=loginbg, background="#0d0d0b").place(x=40, y=40)
    heading = Label(sub, text="Sign in", fg="white", background="#0d0d0b", font=("Microsoft YaHei UI Light", 27, "bold")).place(x=530, y=150)

    def access(event=None):
        user_usern = usern_text.get()
        user_passw = passw_text.get()

        if user_usern == "admin" and user_passw == "root123":
            welcomepage()
        else:
            print("Try again.")

    def entry_focus_in(event):
        if user.get() == "Username":
            user.delete(0, "end")
            user.config(fg="white")

    def entry_focus_out(event):
        if user.get() == "":
            user.delete(0, "end")
            user.insert(0, "Username")
            user.config(fg="white")

    def entry_focus_in2(event):
        if code.get() == "Password":
            code.delete(0, "end")
            code.config(fg="white", show="*")

    def entry_focus_out2(event):
        if code.get() == "":
            code.delete(0, "end")
            code.insert(0, "Password")
            code.config(fg="white", show="")

    def toggle_pass_vsblty():
        current_show = code.cget("show")
        if current_show == "":
            code.config(show="*")
            toggle_button.config(text="Show")
        else:
            code.config(show="")
            toggle_button.config(text="Hide")

    usern_text = StringVar()
    user = Entry(sub, textvariable=usern_text, width=25, fg="grey", border=0, background="#0d0d0b", font=("Microsoft YaHei UI Light", 9))
    user.place(x=500, y=230)
    user.insert(0, "Username")
    user.bind("<FocusIn>", entry_focus_in)
    user.bind("<FocusOut>", entry_focus_out)
    Frame(sub, width=200, height=2, background="#00fffc").place(x=500, y=250)

    passw_text = StringVar()
    code = Entry(sub, textvariable=passw_text, width=25, fg="grey", border=0, background="#0d0d0b", font=("Microsoft YaHei UI Light", 9))
    code.place(x=500, y=270)
    code.insert(0, "Password")
    code.bind("<FocusIn>", entry_focus_in2)
    code.bind("<FocusOut>", entry_focus_out2)
    toggle_button = Button(sub, text="Show", border=0,fg="#00fffc", background="#0d0d0b", command=toggle_pass_vsblty, relief="raised")
    toggle_button.place(x=665, y=265)
    Frame(sub, width=200, height=2, background="#00fffc").place(x=500, y=290)

    loginbtn = Button(sub, text="Log in", width=20, fg="black", background="#00fffc", font=("Midcrosoft YaHei UI Light", 10, "bold"), border=0, relief="raised", command=access)
    loginbtn.place(x=515, y=320)
    main.bind("<Return>", access)
    
